import React, { useState } from 'react';
import Comment from './Comment';
import './Question.css'; // Import the CSS file

const Question = ({ question }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [upvotes, setUpvotes] = useState(question.upvotes);
    const [downvotes, setDownvotes] = useState(question.downvotes);

    const handleAddComment = () => {
        if (!newComment.trim()) return;
        const updatedComments = [...comments, newComment];
        setComments(updatedComments);
        setNewComment('');
    };

    const handleUpvote = () => {
        setUpvotes(upvotes + 1);
    };

    const handleDownvote = () => {
        setDownvotes(downvotes + 1);
    };

    return (
        <div className="question-container">
            <h3 className="question-text">{question.text}</h3>
            <div className="button-container">
                <button onClick={handleUpvote}>Upvote ({upvotes})</button>
                <button onClick={handleDownvote}>Downvote ({downvotes})</button>
            </div>
            <div>
                <input
                    type="text"
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    className="comment-input"
                    placeholder="Add a comment..."
                />
                <button onClick={handleAddComment} className="comment-button">Comment</button>
            </div>
            <div className="comment-container">
                {comments.map((comment, index) => (
                    <div key={index} className="comment">
                        <p>{comment}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Question;
