import React, { useState, useEffect } from 'react';
import Comment from './Comment';
import './Question.css'; // Import the CSS file
import AddBoxRoundedIcon from '@mui/icons-material/AddBoxRounded';
import { Button } from '@mui/material';
import ArrowUpwardRoundedIcon from '@mui/icons-material/ArrowUpwardRounded';
import ArrowDownwardRoundedIcon from '@mui/icons-material/ArrowDownwardRounded';

const Question = ({ question, onVote }) => {
    const [comments, setComments] = useState([]);
    const [voted, setVoted] = useState(null);
    const [newComment, setNewComment] = useState('');
    const [upvotes, setUpvotes] = useState(question.upvotes);
    const [downvotes, setDownvotes] = useState(question.downvotes);

    useEffect(() => {
        fetchComments();
    }, [question.id]);

    const fetchComments = async () => {
        try {
            const response = await fetch(`http://localhost:8080/api/comments/${question.id}`);
            if (!response.ok) {
                throw new Error('Failed to fetch comments');
            }
            const data = await response.json();
            setComments(data);
        } catch (error) {
            console.error('Error fetching comments:', error);
        }
    };

    const handleAddComment = async () => {
        if (!newComment.trim()) return;
        try {
            const response = await fetch('http://localhost:8080/api/comments', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ text: newComment, questionId: question.id }),
            });
            if (!response.ok) {
                throw new Error('Failed to add comment');
            }
            setNewComment('');
            fetchComments(); // Refresh comments after adding a new one
        } catch (error) {
            console.error('Error adding comment:', error);
        }
    };

    const handleUpvote = () => {
        if (voted === null) {
            setUpvotes(prevUpvotes => prevUpvotes + 1);
            setVoted('up');
            onVote(question.id, 'upvote');
        }
    };

    const handleDownvote = () => {
        if (voted === null) {
            setDownvotes(prevDownvotes => prevDownvotes + 1);
            setVoted('down');
            onVote(question.id, 'downvote');
        }
    };

    return (
        <div className="questions-list">
            <li>
                <div className="question-avatar"><img src="http://i9.photobucket.com/albums/a88/creaticode/avatar_1_zps8e1c80cd.jpg" alt=""/></div>
                <div className="question-box">
                    <div className="question-head">
                        <h6 className="question-name by-author">test user</h6>
                        <Button variant={"contained"} startIcon={<ArrowDownwardRoundedIcon/>} onClick={handleDownvote} disabled={voted !== null}>{downvotes}</Button>
                        <Button variant={"contained"} startIcon={<ArrowUpwardRoundedIcon/>} onClick={handleUpvote} disabled={voted !== null}>{upvotes}</Button>
                    </div>
                    <div className="question-content">{question.content}</div>
                    <div className="comment-box1">
                        <input
                            type="text"
                            value={newComment}
                            onChange={(e) => setNewComment(e.target.value)}
                            className="comment-input"
                            placeholder="Add a comment..."
                        />
                        <Button startIcon={<AddBoxRoundedIcon/>} onClick={handleAddComment} className="comment-button"></Button>
                    </div>
                </div>
            </li>
            <div className="comment-container">
                {comments.map((comment, index) => (
                    <Comment key={index} comment={comment}/>
                ))}
            </div>
        </div>
    );
};

export default Question;
