import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Question from './Question';
import QuestionForm from './QuestionForm';
import './ForumPage.css';

const ForumPage = () => {
    const [questions, setQuestions] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get('http://localhost:8080/Question');
                setQuestions(response.data);
            } catch (error) {
                console.error('Error fetching questions:', error);
            }
        };

        fetchQuestions();
    }, []);

    const handleAddQuestion = (newQuestion) => {
        setQuestions([...questions, newQuestion]);
        closeModal();
    };

    const openModal = () => {
        setIsModalOpen(true);
    };

    const toggleModal = () => {
        setIsModalOpen(!isModalOpen);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    const calculateTotalScore = (question) => {
        return question.upvotes - question.downvotes;
    };

    const sortedQuestions = questions.slice().sort((a, b) => {
        return calculateTotalScore(b) - calculateTotalScore(a);
    });

    return (
        <div className="container">
            <h1>Welcome to our Forum page</h1>
            <button onClick={openModal}>Add Question</button>
            {isModalOpen && (
                <div className="modal-overlay" onClick={toggleModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <span className="close" onClick={toggleModal}>&times;</span>
                        <QuestionForm onAddQuestion={handleAddQuestion} />
                    </div>
                </div>
            )}
            <div className="question-list">
                {sortedQuestions.map((question, index) => (
                    <Question key={question.id || index} question={question} />
                ))}
            </div>
        </div>
    );
};

export default ForumPage;
