import React, { useState } from 'react';
import Comment from './Comment';
import './Question.css'; // Import the CSS file

const Question = ({ question }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');

    const handleAddComment = () => {
        if (!newComment.trim()) return;
        const updatedComments = [...comments, { text: newComment, upvotes: 0, downvotes: 0 }];
        setComments(updatedComments);
        setNewComment('');
    };

    return (
        <div className="question-container">
            <div className="question-avatar"><img src="http://i9.photobucket.com/albums/a88/creaticode/avatar_1_zps8e1c80cd.jpg" alt="" /></div>
            <div className="question-box">
                <div className="question-head">
                    <h6 className="comment-name by-author"><a href="http://creaticode.com/blog">test user</a></h6>
                    <button>Upvote ({question.upvotes})</button>
                    <button>Downvote ({question.downvotes})</button>
                </div>
                <div className="question-content">{question.text}</div>
            </div>
            <div className="comment-box">
                <input
                    type="text"
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        className="comment-input"
                        placeholder="Add a comment..."
                />
                <button onClick={handleAddComment} className="comment-button">Comment</button>
            </div>
            <div className="comment-container">
                {comments.map((comment, index) => (
                    <Comment key={index} comment={comment}/>
                ))}
            </div>
        </div>
    );
};

export default Question;
