import React, { useState } from 'react';
import './Question.css'; // Import the CSS file
import AddBoxRoundedIcon from '@mui/icons-material/AddBoxRounded';
import { Button } from '@mui/material';
import ArrowUpwardRoundedIcon from '@mui/icons-material/ArrowUpwardRounded';
import ArrowDownwardRoundedIcon from '@mui/icons-material/ArrowDownwardRounded';

const Question = ({ question }) => {
    const [voted, setVoted] = useState(null);
    const [upvotes, setUpvotes] = useState(question.upvotes);
    const [downvotes, setDownvotes] = useState(question.downvotes);

    const handleUpvote = () => {
        if (voted === null) {
            setUpvotes(prevUpvotes => prevUpvotes + 1);
            setVoted('up');
        }
    };

    const handleDownvote = () => {
        if (voted === null) {
            setDownvotes(prevDownvotes => prevDownvotes + 1);
            setVoted('down');
        }
    };

    return (
        <div className="questions-list">
            <li>
                <div className="question-avatar">
                    <img src="http://i9.photobucket.com/albums/a88/creaticode/avatar_1_zps8e1c80cd.jpg" alt="" />
                </div>
                <div className="question-box">
                    <div className="question-head">
                        <h6 className="question-name by-author">test user</h6>
                        <Button variant={"contained"} startIcon={<ArrowDownwardRoundedIcon />} onClick={handleDownvote} disabled={voted !== null || downvotes > question.downvotes}>({downvotes})</Button>
                        <Button variant={"contained"} startIcon={<ArrowUpwardRoundedIcon />} onClick={handleUpvote} disabled={voted !== null || upvotes > question.upvotes}>({upvotes})</Button>
                    </div>
                    <div className="question-content">{question.text}</div>
                    <div className="comment-box1">
                        <input
                            type="text"
                            value={''}
                            onChange={() => {}} // Dummy onChange handler
                            className="comment-input"
                            placeholder="Add a comment..."
                        />
                        <Button startIcon={<AddBoxRoundedIcon />} className="comment-button"></Button>
                    </div>
                </div>
            </li>
        </div>
    );
};

export default Question;
