import React, { useState } from 'react';
import Question from './Question';
import QuestionForm from './QuestionForm';
import './ForumPage.css';

const ForumPage = () => {
    const [questions, setQuestions] = useState([]);
    const [showAddModal, setShowAddModal] = useState(false);
    
    const handleAddQuestion = (newQuestion) => {
        setQuestions([...questions, newQuestion]);
    };

    // Function to calculate the total score (upvotes minus downvotes) of a question
    const calculateTotalScore = (question) => {
        return question.upvotes - question.downvotes;
    };

    // Sort the questions based on total score
    const sortedQuestions = questions.slice().sort((a, b) => {
        return calculateTotalScore(b) - calculateTotalScore(a);
    });

    return (
        <div className="container"> {/* Apply container class */}
            <h1>Wlecome to our Forum Page</h1>
            <button onClick={openModal}>Ask a Question</button>
            {showAddModal && (
                <div className="modal-wrapper">
                    <div className="modal" onClick={() => setShowAddModal(false)}>
                        <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <span className="close" onClick={() => setShowAddModal(false)}>
                &times;
              </span>
                            <h2>{editingIndex !== null ? "Edit Funky Journal" : "Add New Funky Journal"}</h2>
                            <input
                                type="text"
                                placeholder="Title"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}
                            />
                            <textarea
                                className="journal-textarea"
                                placeholder="Write your funky journal entry here..."
                                value={newEntry}
                                onChange={(e) => setNewEntry(e.target.value)}
                            />
                            <div className="date-container">
                                <p>Date: {new Date().toLocaleDateString()}</p>
                            </div>
                            <button className="add-entry-btn"
                                    onClick={editingIndex !== null ? editJournalEntry : addJournalEntry}>
                                {editingIndex !== null ? "Save Changes" : "Add Funky Entry"}
                            </button>
                        </div>
                    </div>
                </div>
            )}
            <div className="question-list"> {/* Apply question-list class */}
                {sortedQuestions.map((question, index) => (
                    <Question key={index} question={question} />
                ))}
            </div>
        </div>
    );
};

export default ForumPage;
